mapper.select:

select 

from cons_useat_stk_t
where cons_useatstk_id = #{consUseatstkId,jdbcType=BIGINT}

  and update_dt = #{updateDt,jdbcType=TIMESTAMP}
  and update_user_id = #{updateUserId,jdbcType=VARCHAR}


  and LOGIC_DELETE_FLG = '0'


  FOR UPDATE NOWAIT

mapper.delete:

delete from cons_useat_stk_t
where cons_useatstk_id = #{consUseatstkId,jdbcType=BIGINT}

  and update_dt = #{updateDt,jdbcType=TIMESTAMP}
  and update_user_id = #{updateUserId,jdbcType=VARCHAR}


  and LOGIC_DELETE_FLG = '0'

