mapper.select:

select 

from seat_stock_t
where seat_stock_id = #{seatStockId,jdbcType=BIGINT}

  and update_dt = #{updateDt,jdbcType=TIMESTAMP}
  and update_user_id = #{updateUserId,jdbcType=VARCHAR}


  and LOGIC_DELETE_FLG = '0'


  FOR UPDATE NOWAIT

mapper.delete:

delete from seat_stock_t
where seat_stock_id = #{seatStockId,jdbcType=BIGINT}

  and update_dt = #{updateDt,jdbcType=TIMESTAMP}
  and update_user_id = #{updateUserId,jdbcType=VARCHAR}


  and LOGIC_DELETE_FLG = '0'

